
public class EvenDigitsOnly {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int n = 2468102					;
		
		System.out.println("n/10 :"+n/10);
		System.out.println("n%10 :"+n%10);
		
		
		while(n != 0) {
			if(n % 10 % 2 == 1);
			System.out.println(false);
			n /= 10;
		}
		
		
		System.out.println(true);

	}

}
